import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from "../styles/Events.module.css";

const Events = ({ currentUser }) => {
  const [events, setEvents] = useState([]);
  const [addEventOpen, setAddEventOpen] = useState(false);
  const [openForms, setOpenForms] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch events from database
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:8080/api/events');
        const data = await response.json();
        setEvents(data);
      } catch (err) {
        setError('Failed to load events');
      } finally {
        setLoading(false);
      }
    };
    fetchEvents();
  }, []);

  const toggleAddEventForm = () => setAddEventOpen(!addEventOpen);

  const addCustomEvent = async (e) => {
    e.preventDefault();
    const formData = {
      name: e.target.customEventName.value.trim(),
      date: e.target.customEventDate.value,
      time: e.target.customEventTime.value,
      place: e.target.customEventPlace.value.trim(),
      description: e.target.customEventDesc.value.trim()
    };

    try {
      const response = await fetch('http://localhost:8080/api/events', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (!response.ok) throw new Error('Failed to create event');

      const newEvent = await response.json();
      setEvents(prev => [newEvent, ...prev]);
      e.target.reset();
      setAddEventOpen(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSignup = async (eventId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/events/${eventId}/signup`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: currentUser._id }) // Assuming you have user auth
      });

      if (!response.ok) throw new Error('Signup failed');

      const updatedEvent = await response.json();
      setEvents(prev => 
        prev.map(event => 
          event._id === updatedEvent._id ? updatedEvent : event
        )
      );
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className={styles.eventsContainer}>
      <h2>Events</h2>
      {loading && <p>Loading events...</p>}
      {error && <p className={styles.error}>{error}</p>}

      {/* Button to open Add Event Form */}
      <button onClick={toggleAddEventForm}>
        {addEventOpen ? "Cancel" : "Add Event"}
      </button>

      {/* Add Event Form */}
      {addEventOpen && (
        <form onSubmit={addCustomEvent} className={styles.addEventForm}>
          <input name="customEventName" placeholder="Event Name" required />
          <input name="customEventDate" type="date" required />
          <input name="customEventTime" type="time" required />
          <input name="customEventPlace" placeholder="Place" required />
          <textarea name="customEventDesc" placeholder="Description" />
          <button type="submit">Create Event</button>
        </form>
      )}

      {/* Events List */}
      <ul className={styles.eventList}>
        {events.map(event => (
          <li key={event._id} className={styles.eventItem}>
            <h3>{event.name}</h3>
            <p>
              <strong>Date:</strong> {new Date(event.date).toLocaleDateString()}<br />
              <strong>Time:</strong> {event.time}<br />
              <strong>Place:</strong> {event.place}<br />
              <strong>Description:</strong> {event.description}
            </p>
            <div>
              <strong>Participants:</strong>
              <ul>
                {event.participants && event.participants.length > 0 ? (
                  event.participants.map((participant, idx) => (
                    <li key={idx}>
                      {/* If participant is a user object, display name/email; else, display ID */}
                      {participant.username || participant.email || participant}
                    </li>
                  ))
                ) : (
                  <li>No participants yet.</li>
                )}
              </ul>
            </div>
            {/* Signup Button */}
            {currentUser && !event.participants?.includes(currentUser._id) && (
              <button onClick={() => handleSignup(event._id)}>
                Sign Up
              </button>
            )}
            {currentUser && event.participants?.includes(currentUser._id) && (
              <span className={styles.signedUp}>You are registered</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );

};

export default Events;
